import React, { Component } from 'react'
import './Filters.scss'

class Filters extends Component{
    state = {
        title: ''
    }


    titleHandler(e){
        let newTitle = e.target.innerHTML;
        if(newTitle != 'All'){
            this.setState({
                title: newTitle
            })
        }else{
            this.setState({
                title: ''
            })
        }
    }

    render(){


        return(
            <div className="filters-wrapper">
                <h1 className={this.state.title ? "page-title title-active" : "page-title"}>
                    {this.state.title}
                </h1>

                <div className="filters">
                    <ul className="filters-ul">
                        <li className="filter-item" onClick={this.titleHandler.bind(this)}>All</li>
                        <li className="filter-item" onClick={this.titleHandler.bind(this)}>Furniture</li>
                        <li className="filter-item" onClick={this.titleHandler.bind(this)}>Kitchen</li>
                        <li className="filter-item" onClick={this.titleHandler.bind(this)}>Lighting</li>
                        <li className="filter-item" onClick={this.titleHandler.bind(this)}>Tableware</li>
                    </ul>
                </div>

                <div className="filters-navigation">Home/{this.state.title}</div>
            </div>
        )
    }
}

export default Filters
