import React, { Component } from 'react'
import './Products.scss'


class Product extends Component{
    constructor(props){
        super(props)
    }

    state = {
        img: this.props.img
    }

    hoverHandler = () => {
        let hoverImg = this.props.hover

        this.setState({
            img: hoverImg
        })
    }

    leaveHandler = () => {
        let img = props.img;

        this.setState({img})
    }


    render() {
        return(
            <div className="card" onMouseEnter={this.hoverHandler} onMouseLeave={this.leaveHandler}>
                <div className="photo-wrapper">
                    <img src={this.state.img} className="card-photo" alt="prod_photo"/>
                </div>
                <div className="card-footer">
                    <h3 className="card-name">{this.props.name}</h3>
                    <h3 className="card-price">${this.props.price}</h3>
                </div>
            </div>
        )
    }

}

export default Product
