import React, { Component } from 'react'
import './Slider.scss';

class Slider extends Component{
    constructor(props){
        super(props)
    }

    state = {
        count: 1
    }

    sliderHandler = (e) => {
        if(e.target.classList.contains('next')){
            if(state.count < props.photos.length){
                document.querySelector('.slider').style.transform = `translateX(-${state.count * 100}%)`
                state.count +=1;
            }

        }else{
            if(state.count > 0){
                document.querySelector('.slider').style.transform = `translateX(${state.count * 100}%)`
                state.count--;
            }
        }

    }


    render(){
        return(
            <div className="slider-wrapper">
                    <div className="slider-btn prev" onClick={this.sliderHandler}>&lt;</div>
                <div className="slider">
                    {props.photos.map((photo, index) => {
                        return(
                            <img src={photo} key={index} alt={index} className="slider-item"></img>
                        )
                    })}
                </div>
                    <div className="slider-btn next" onClick={this.sliderHandler}>&gt;</div>
                <div className="all-photo">
                    {props.photos.map((photo, index) => {
                        return(
                            <img src={photo} key={index} alt={index} className="photo-item"></img>
                        )
                    })}
                </div>
            </div>
        )
    }
}


export default Slider
