import React, { Component } from 'react'
import './Products.scss'


class Product extends Component{
    constructor(props){
        super(props)
    }

    state = {
        img: this.props.props.img,
        isHover: false
    }

    hoverHandler = () => {
        const hoverImg = this.props.props.hover
        const hover = !this.state.isHover

        this.setState({
            img: hoverImg,
            isHover: hover,
        })
    }

    leaveHandler = () => {
        const defaultImg = this.props.props.img;
        const hover = !this.state.isHover

        this.setState({
            img: defaultImg,
            isHover: hover,
        })
    }

    classHandler = () => {
        if(this.state.isHover){
            return "card-hover"
        }else{
            return ""
        }
    }


    render() {

        return(
            <div className={
                this.props.props.view === 'banner' ? "card card-banner" : "card"
            && this.classHandler
            }
            onMouseEnter={this.hoverHandler} 
            onMouseLeave={this.leaveHandler}>
                <div className="photo-wrapper">
                    <img src={this.state.img} className="card-photo" alt="prod_photo"/>
                </div>
                <div className="card-footer">
                    <h3 className="card-name">{this.props.props.name}</h3>
                    <h3 className="card-price">${this.props.props.price}</h3>
                </div>
            </div>
        )
    }

}

export default Product
