import React from 'react'
import './Slider.scss';

export default (props) => {

    function slider(e){
        if(e.target.classList.contains('next')){
            console.log(91);
        }else{
            console.log(11);
        }
    }


    return(
        <div className="slider-wrapper">
            <div className="slider">
                <div className="slider-btn prev" onClick={slider}>&lt;</div>
                {props.photos.map((photo, index) => {
                    return(
                        <img src={photo} key={index} alt={index} className="slider-item"></img>
                    )
                })}
                <div className="slider-btn next" onClick={slider}>&gt;</div>
            </div>
            <div className="all-photo">
                {props.photos.map((photo, index) => {
                    return(
                        <img src={photo} key={index} alt={index} className="photo-item"></img>
                    )
                })}
            </div>
        </div>
    )
}
