import React, { Children, createElement } from 'react'
import './ProdName.scss'

const ProdName = (props) => {

    const getNavHandler = (elem) => {
        return(
            <p className="name-nav">
                <span onClick={() => props.navHandl('span-home')} className="span-home" > Home</span> / 
                <span onClick={() => props.navHandl('span-style')} className="span-style" >{elem.filterProps}</span> / 
                <span>{elem.name}</span>
            </p>
        )
    }


    const getPriceHandler = (elem) => {
        return(
            <div className="price-wrap">
                <h1>{elem.name}</h1>
                <h3>${elem.price}</h3>
            </div>
        )
    }


    const getCountHandler = () => {
        return(
            <div className="count-wrap">
                <div className="input-wrapper">
                    <input type="number" value="1" className="input-count"></input>
                    <div className="add-btn">Add to cart</div>
                </div>
                <div className="social-icons">
                    <img src="https://www.flaticon.com/svg/static/icons/svg/733/733579.svg"></img>
                    <img src="https://www.flaticon.com/svg/static/icons/svg/733/733547.svg"></img>
                    <img src="https://www.flaticon.com/svg/static/icons/svg/733/733585.svg"></img>
                </div>
            </div>
        )
    }

    return(
        <div className="name-wrapper">
            {getNavHandler(props.prod)}

            {getPriceHandler(props.prod)}

            {getCountHandler()}
        </div>
    )
}


export default ProdName
