import { Component } from 'react';
import { Route, Link } from "react-router-dom";
import './App.scss';
import Header from './Header/Header';
import ProductPage from "./ProductPage/ProductPage";
import allProducts from './woocomerce/database'
import Footer from './Footer/Footer'
import Cart from './Cart/Cart'
import MainPage from "./MainPage";
import MainProductPage from "./MainProductPage";


class App extends Component {
	state = {
		title: '',
		products: [...allProducts],
		showProdPage: false,
		currProdPage: null,
		currProdPageID: null,
		pagination: [1, 14],
		reviews: false,
		moneyInCart: 0,
		cart: [],
		showCart: false,
	}

	// Open product page
	openProdPageHandler = (id) => {
		const currentProd = this.state.products.find(elem => {
			if(elem.id === id){
				return elem
			}
		})

		this.setState({
			showProdPage: true,
			currProdPage: currentProd,
			currProdPageID: currentProd.id,
		})
	}

	descriptionHandler = () => {
		this.setState({
			reviews: !this.state.reviews
		})
	}

	//Navigation in filters
	changeNavHandler = (e) => {
		if(e.target.className === 'span-home' || e.target.className == 'header'){
			this.setState({
				showProdPage: false,
				products: allProducts,
			})
		}else if(e.target.className == 'span-style'){
			this.filterHandler(e)
		}
	}

	//Pagitation
	paginationHandler = (e) => {
		const standartIndex = [1, 14];
		let page = e.target.innerHTML;

		for(let i = 1; i < page; i++){
			standartIndex[0] += 14;
			standartIndex[1] += 14
		}

		document.querySelectorAll('.pag-btn').forEach(elem => {
			elem.classList.remove('active')
		})

		e.target.classList.add('active')

		if(e.target.className.includes('pag-btn')){
			this.setState({
				pagination: standartIndex
			})
		}
	}


	//Filters
	filterHandler = (e) => {
		//Set new title for filters
		let newTitle = e.target.innerHTML;


		//Set new displays product if filters is active
        if(newTitle !== 'All'){
			let filteredProd = allProducts.filter(elem => {
				if(elem.filterProps == e.target.innerHTML){
					return elem
				}
			})

            this.setState({
				products: filteredProd,
				title: newTitle,
				showProdPage: false,
			})
			//if filters no active set default page
        }else{
			this.setState({
				showProdPage: false,
				products: allProducts,
				title: '',
            })
		}
	}


	//CART -----------------------------------
	addToCart = (id) => {
		let newProdInCart = allProducts.filter(elem => {
			return elem.id === id
		})
		console.log(newProdInCart[0].name);
		let cart = this.state.cart;

		if(cart.includes(newProdInCart[0])){
			//ДОПИСАТЬ ЧТОБЫ ДОБАВЛЯЛО В кАРТ 
		}else{
			cart.push(newProdInCart[0])
		}

		let money = 0;
		cart.forEach(elem => {money += elem.price})

		this.setState({
			cart: cart,
			moneyInCart: money,
		})
	}

	openCartPage = () => {
		this.setState({
			showCart: !this.state.showCart
		})
	}


	componentDidUpdate = () => {
		// window.scrollTo({top: 0, behavior: 'smooth'})
	}

    render() {

		const pageCounter = Math.ceil(this.state.products.length / 14)

		if(this.state.showProdPage){
			//SINGLE PROdUCT PAGE -------------------------
			return(
				<div className="App">
					<Header navHandl={this.changeNavHandler.bind(this)}
					moneyCart={this.state.moneyInCart}
					countProductInCart={this.state.countProdInCart}
					openCart={this.openCartPage.bind(this)}/>

						<ProductPage props={this.state.currProdPage}
						navHandl={this.changeNavHandler.bind(this)}
						descHandler={this.descriptionHandler.bind(this)}
						reviews={this.state.reviews}/>

					<Footer />
				</div>
			)
		}else if(this.state.showCart){
			//CARt PAGE=------------------- --------------------------------
			return(
				<div className="App">
					<Header navHandl={this.changeNavHandler.bind(this)}
					moneyCart={this.state.moneyInCart}
					countProductInCart={this.state.cart.length}
					openCart={this.openCartPage.bind(this)}/>

					<div className="products-wrapper">
					<Cart cart={this.state.cart}/>
					</div>

					<Footer />
				</div>
			)
		}else{
			//DEFAULT SHOPPING PAGE --------------------------------
			return(
				<div className="App">

					<Header navHandl={this.changeNavHandler.bind(this)}
					moneyCart={this.state.moneyInCart}
					countProductInCart={this.state.cart.length}
					openCart={this.openCartPage.bind(this)}/>

					{/* ROUTE FOR SHOW DEFAUL SHOPPING PAGE (MAIN PAGE) */}
					<Route exact path="/" component={
						MainPage.bind(this, {
							// Props for FILTERS
						filterHandler: this.filterHandler.bind(this), 
						title: this.state.title,
							// Props for cards with product
						products: [...this.state.products],
						pagination: this.state.pagination,
						openPage: this.openProdPageHandler,
						addToCart: this.addToCart.bind(this),
							// Props for pagination
						pageCounter: pageCounter,
						pagiHandler: this.paginationHandler.bind(this),
					})} />


					{/* ROUTE FOR SHOW SINGLE PRODUCT PAGE */}
					<Route exact path={this.state.currProdPageID} component={
						ProductPage.bind(this, {
							//Props for product page
						props: this.state.currProdPage,
						navHandl: this.changeNavHandler.bind(this),
						descHandler: this.descriptionHandler.bind(this),
						reviews: this.state.reviews,
					})} />

					<Footer />
				</div>
			)
		}



    }
}

export default App;
