import React, { Component } from 'react'
import './Products.scss'
import {ReactComponent as Cart} from './cart.svg'


class Product extends Component{
    state = {
        img: this.props.img,
        isHover: false
    }

    hoverHandler = (e) => {
        const hoverImg = this.props.hover
        const hover = !this.state.isHover

        this.setState({
            img: hoverImg,
            isHover: hover,
        })
    }

    leaveHandler = (e) => {
        const defaultImg = this.props.img;
        const hover = !this.state.isHover

        this.setState({
            img: defaultImg,
            isHover: hover,
        })
    }



    render() {

        return(
            <div className={this.props.view === 'banner' ? "card card-banner" : "card"}
            onMouseEnter={this.hoverHandler} 
            onMouseLeave={this.leaveHandler}
            >
                <div className="photo-wrapper" onClick={this.props.openPage}>
                    <img src={this.state.img} className="card-photo" alt="prod_photo"/>
                </div>
                <div className="card-footer">
                    <h3 className="card-name">{this.props.name}</h3>
                    <h3 className="card-price">${this.props.price}</h3>
                    <a href="#" className={this.state.isHover ? "hover-cart card-hover" : "hover-cart"}><Cart /></a>
                </div>
            </div>
        )
    }

}

export default Product
