import React from "react";
import './Desc.scss';

const Desc = (props) => {
    console.log(props.props);
    if(props.rewievs){
        return(
            <h1>Rewiev</h1>
            )
        }else{
            return(
                <div className="desc-wrapper">
                <h1>Description</h1>

                {getDesc(props.props)}
            </div>
        )
    }
}

function getDesc(elem){
    for(let keys in elem.desc){
        // console.log(keys);
        // console.log(elem.desc[`${keys}`]);

        return(
            <div>
                <p>{keys[0].toUpperCase() + keys.slice(1)}: </p> <p>{elem.desc[`${keys}`]}</p>
            </div>
        )
    }

}

export default Desc
