import React from "react";
import './Desc.scss';

const Desc = (props) => {
    if(props.reviews){
        return(
            <h1 className="desc-wrapper">Reviews</h1>
            )
        }else{
            return(
                <div className="desc-wrapper">
                    <div className={props.reviews ? "desc-btn" : "desc-btn active"} onClick={props.descHandler}>Description</div> 
                    <div className={props.reviews ? "desc-btn active" : "desc-btn"}  onClick={props.descHandler}>Reviews(0)</div>

                    <h1>Description</h1>

                    {getDesc(props.props)}
            </div>
        )
    }
}

function getDesc(elem){

    let array = Object.entries(elem.desc);

    return(
        array.map(elem => {
            if(elem[0] !== 'prod_info'){
                return(
                    <h3 className="desc-row">{elem[0].toUpperCase()}: <br/>
                        <p className="desc">{elem[1]}</p>
                    </h3>
                )
            }else{
                return(
                    <h3 className="desc-row">PRODUCT INFO: <br/>
                        <p className="desc">{elem[1]}</p>
                    </h3>
                )
            }

        })
    )
}

export default Desc
