import React from "react";
import './Desc.scss';

const Desc = (props) => {
    if(props.rewievs){
        return(
            <h1>Rewiev</h1>
            )
        }else{
            return(
                <div className="desc-wrapper">
                <h1>Description</h1>

                {getDesc(props.props)}
            </div>
        )
    }
}

function getDesc(elem){

    let array = Object.entries(elem.desc);

    return(
        array.map(elem => {
            if(elem[0] !== 'prod_info'){
                return(
                    <p>{elem[0][0].toUpperCase() + elem[0].slice(1)}: <br/>{elem[1]}</p>
                )
            }else{
                return(
                    <p>Product Info: <br/>{elem[1]}</p>
                )
            }

        })
    )
}

export default Desc
