import React, { Component } from 'react'
import './Products.scss'


class Product extends Component{
    state = {

    }

    hoverHandler(){
        console.log(11);
    }

    render(){

        return(
            <div className={this.props.view == 'banner' ? "card card-banner" : "card"} onMouseEnter={this.hoverHandler}>
                <div className="photo-wrapper">
                    <img src={this.props.img} className="card-photo"/>
                </div>
                <div className="card-footer">
                    <h3 className="card-name">{this.props.name}</h3>
                    <h3 className="card-price">${this.props.price}</h3>
                </div>
            </div>
        )
    }
}

export default Product
