import React, { Component } from 'react'
import './Header.scss'
// import bag from './src/assets/icons/shopping-bag.svg'


class Header extends Component{
    state = {
        burger: false,
    }

    burgerHandler(){
        this.setState({
            burger: !burger
        })
    }


    render(){


        return(
            <header>
                <a href="#" className="header">OCIN LITE</a>

                <nav>
                    <a href="" className={this.state.burger ? "burger-btn burger-active" : "burger-btn"}
                     onClick={this.burgerHandler.bind(this)}>

                     </a>
                    <ul className="nav-items-wrapper">
                        <li className="nav-item">SHOP</li>
                        <li className="nav-item">BLOG</li>
                        <li className="nav-item">PRODUCTS</li>
                    </ul>

                    <div className="cart-login-wrapper">
                        <div className="login-btn">LOGIN</div>
                        <div className="cart-preview"></div>
                    </div>
                </nav>
            </header>
        )
    }
}


export default Header
